svg.script:
var week = ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'];
var pointLoc = 0;
var vWidth = 740;
var vHeight = 400;
var crossH = 0;
var crossV = 0;
var maxV = 0;
var maxL = 0;
var time24 = 0;
var advX = 1;
var interval = 0;
var hours = 0;
var xpstVis = 1;
var ignoreNext = 0;
var eraseL = 0;
var samp = 1;

rxData = [top.curr_rxData];

function initial(){
	gen_svg();
	
	xpst = [];

	for (n=0; n<7; ++n){
		xpst[n] = E('xpst' + n);
		setText(xpst[n], xps(n+1));
	}
}

function setText(e, text){
	if(e.firstChild)
		e.removeChild(e.firstChild);
	e.appendChild(document.createTextNode(text));
}

function xps(n){
	if(n<5){
		var currUnit;
		x = n*10+40;
		if(top.cookie.get("CoreTmpUnit") == "1"){
			currUnit = " \'F";
			x = x*9/5+32;
		}
		else
			currUnit = " \'C";

		return (x + currUnit);
	}
	else{
		var x = (n-4)*5;
		return (x + " min");
	}
}

function xpsslash(n){
		return ("/");
}

function E(id){
	return document.getElementById(id);

}

function drawData(poly, data, mode, color){
	var i;
	var pt;
	var x, y;
	var d, j;

	maxV = 120;
	maxL = 180;
	advX = vWidth / maxL;
	dataDiv = 2;

	if (data.length == 0) return;

	x = 0;
	if (mode == 0) {
		poly.setAttribute('fill-opacity', '0');
		pt = '0,' + vHeight;
	}
	else {
		poly.setAttribute('fill-opacity', '0');
		pt = '';
	}
	poly.setAttribute('stroke', color);

	for (i = data.length - maxL; i < data.length; ++i) {
		if (i < 0) {
			d = 0;
		}
		else {
			d = (data[i]-40)*8;
		}

		y = vHeight - d;
		pt += ' ' + Math.floor(x) + ',' + y;
		x += advX;
	}

	pt += ' ' + (vWidth + 5) + ',' + y + ' ' + (vWidth + 10) + ',' + vHeight;
	poly.setAttribute('points', pt);
}

function gen_svg(){
	polyCoreTmp_2 = E('polyCoreTmp_2');
	polyCoreTmp_5 = E('polyCoreTmp_5');
	polyCoreTmp_cpu = E('polyCoreTmp_cpu');
	top.coreTmp_2[top.coreTmp_2.length] = top.curr_coreTmp_2;
	top.coreTmp_5[top.coreTmp_5.length] = top.curr_coreTmp_5;
	top.coreTmp_cpu[top.coreTmp_cpu.length] = top.curr_coreTmp_cpu;
	updateSVG();
}

function updateSVG(){
	if (top.curr_coreTmp_2>0) drawData(polyCoreTmp_2, top.coreTmp_2, 0, "#FF9000");
	if (top.curr_coreTmp_5>0) drawData(polyCoreTmp_5, top.coreTmp_5, 0, "#33CCFF");
	if (top.curr_coreTmp_cpu>0) drawData(polyCoreTmp_cpu, top.coreTmp_cpu, 0, "#00FF33");
	//drawData(polyRx, rxData, 0, "#33CCFF");
	setTimeout('gen_svg();', 5000);
}
